import styled from "styled-components";

const StyleDiv = styled.div`
   border: 2px solid black;
   border-radius: 5px;
   width: 200px;
   background-color: green;
   color: black;
   margin: 20px;
`
const StyleImg = styled.img`
   width: 100%
`

const StyleBtn = styled.button`
   background-color: green;
   color: black;
`

export default function Card(props) {
   return (
      <StyleDiv>
         <StyleBtn onClick={()=>{props.onClose()}}>X</StyleBtn>
         <p>{props.name}</p>
         <p>{props.status}</p>
         <p>{props.species}</p>
         <p>{props.gender}</p>
         <p>{props.origin.name}</p>
         <StyleImg src={props.image} alt="" />
      </StyleDiv>
   );
}
