import { ADD_FAV, FILTER, REMOVE_FAV } from "./action-type";

export const initialState = {
    myFavorites: [],
    allCharacters: []
}

const reducer = (state = initialState, { type, payload }) => {
    switch (type) {
        case ADD_FAV:
            return {
                ...state,
                myFavorites: [...state.allCharacters, payload],
                allCharacters: [...state.allCharacters, payload]
            }

        case REMOVE_FAV:
            return {
                ...state,
                myFavorites: state.myFavorites.filter(fav => fav.id !== Number(payload))
            }

        case FILTER:
            const allCharactersFiltered = state.allCharacters.filter(character =>{ character.gender === payload})
            return {
                ...state,
                myFavorites: allCharactersFiltered
            }

        case ORDER:

        default:
            return { ...state };
    }
}

export default reducer;
